def group_jobs_by_type(job_instances, K):
    grouped_jobs = {}

    for job in job_instances:
        job_type = job.get('job_type')
        if job_type in grouped_jobs:
            grouped_jobs[job_type].append(job)
        else:
            grouped_jobs[job_type] = [job]

        if len(grouped_jobs[job_type]) == K:
            grouped_jobs[job_type] = [grouped_jobs[job_type]]

    return grouped_jobs
