    public class SQSService : BackgroundService
    {
        protected override async Task ExecuteAsync(CancellationToken stoppingToken)
        {
            while (!stoppingToken.IsCancellationRequested)
            {
                // Process messages from AWS SQS queue
                Console.WriteLine("Processing messages from SQS queue...");

                // Simulate processing by adding a delay
                await Task.Delay(5000, stoppingToken); // Process each message every 5 seconds
