void generateFlatTerrainMesh(float width, float depth, int numSegments) {
    std::vector<float> vertices;
    std::vector<unsigned int> indices;
    std::vector<float> normals;
    std::vector<float> textures;

    float segmentWidth = width / numSegments;
    float segmentDepth = depth / numSegments;

    for (int i = 0; i <= numSegments; ++i) {
        for (int j = 0; j <= numSegments; ++j) {
            float x = j * segmentWidth - width / 2;
            float z = i * segmentDepth - depth / 2;

            vertices.push_back(x);
            vertices.push_back(0.0);
            vertices.push_back(z);

            normals.push_back(0.0);
            normals.push_back(1.0);
            normals.push_back(0.0);

            float u = (float)j / numSegments;
            float v = (float)i / numSegments;
            textures.push_back(u);
            textures.push_back(v);

            if (i < numSegments && j < numSegments) {
                unsigned int index = i * (numSegments + 1) + j;
                indices.push_back(index);
                indices.push_back(index + numSegments + 1);
                indices.push_back(index + numSegments + 2);

                indices.push_back(index);
                indices.push_back(index + numSegments + 2);
                indices.push_back(index + 1);
            }
        }
    }

    // Now you can use the generated vertices, indices, normals, and textures for rendering
}
