```javascript
/**
 * @fileoverview Custom ESLint rule to check for mutating methods in Vue.js lifecycle hooks
 */
module.exports = {
  create: function(context) {
    const vueLifecycleHooks = [
      'beforeCreate', 'created',
      'beforeMount', 'mounted',
      'beforeUpdate', 'updated',
      'beforeDestroy', 'destroyed'
    ];

    function checkMethod(node) {
      const methodBody = node.value.body.body;
      methodBody.forEach(statement => {
        if (statement.type === 'CallExpression') {
          if (
            statement.callee.type === 'MemberExpression' &&
            statement.callee.object.type === 'ThisExpression'
          ) {
            context.report({
              node: statement,
              message: `Avoid mutating state directly in lifecycle hook ${node.parent.key.name}`
            });
          }
        }
      });
    }

    return {
      MethodDefinition(node) {
        if (
          node.parent &&
          node.parent.type === 'ObjectExpression' &&
          vueLifecycleHooks.includes(node.parent.parent.key.name)
        ) {
          checkMethod(node);
        }
      }
    };
  }
};
```

