```rust
use minifb::{Key, Window, WindowOptions};

const WIDTH: usize = 800;
const HEIGHT: usize = 600;
const GRID_SIZE: usize = 20;

fn main() {
    let mut buffer: Vec<u32> = vec![0; WIDTH * HEIGHT];
    let mut window = Window::new(
        "Grid Clicker - Click on the grid to see coordinates",
        WIDTH,
        HEIGHT,
        WindowOptions::default(),
    )
    .unwrap_or_else(|e| {
        panic!("{}", e);
    });

    while window.is_open() && !window.is_key_down(Key::Escape) {
        if let Some(pos) = window.get_mouse_pos(minifb::MouseMode::Clamp) {
            let x = (pos.0 / (WIDTH as f32 / GRID_SIZE as f32)) as usize;
            let y = (pos.1 / (HEIGHT as f32 / GRID_SIZE as f32)) as usize;

            if x < GRID_SIZE && y < GRID_SIZE {
                println!("Clicked on grid at ({}, {})", x, y);
            }
        }

        for y in 0..HEIGHT {
            for x in 0..WIDTH {
                let color = if x % (WIDTH / GRID_SIZE) == 0 || y % (HEIGHT / GRID_SIZE) == 0 {
                    0xFFFFFF
                } else {
                    0
                };
                buffer[y * WIDTH + x] = color;
            }
        }

        window.update_with_buffer(&buffer, WIDTH, HEIGHT).unwrap();
    }
}
```

