import java.util.regex.Pattern;

public class SQLtoMongoDBConverter {

    public static Document convertToMongoDB(String sqlQuery) {
        Document mongoQuery = new Document();

        // Example translation from SQL WHERE clause to MongoDB filter
        if(sqlQuery.contains("WHERE")) {
            String[] clauses = sqlQuery.split("WHERE");
            String condition = clauses[1].trim();
            if(condition.contains("LIKE")) {
                String[] likeParts = condition.split("LIKE");
                String field = likeParts[0].trim();
                String value = likeParts[1].trim().replaceAll("'", "");
                mongoQuery.put(field, Pattern.compile(value));
            }
            // Add more conditions translations as needed
        }

        return mongoQuery;
    }

    public static void main(String[] args) {
        String sqlQuery = "SELECT * FROM users WHERE name LIKE 'John%'";
        Document mongoQuery = convertToMongoDB(sqlQuery);
        System.out.println("Converted MongoDB query: " + mongoQuery.toJson());
    }
}
```

