    grouped_jobs = {}
    for job in jobs:
        job_type = job.job_type
        if job_type not in grouped_jobs:
            grouped_jobs[job_type] = []
        if len(grouped_jobs[job_type]) == K:
            job_type += '_extra'
            if job_type not in grouped_jobs:
                grouped_jobs[job_type] = []
            grouped_jobs[job_type].append(job)
        else:
            grouped_jobs[job_type].append(job)

