```csharp
using System;
using System.Text.RegularExpressions;

class TokenCounter
{
    public static int CountTokens(string input)
    {
        // Define a regular expression pattern to match tokens (words)
        string pattern = @"\b\w+\b";

        // Create a Regex object with the pattern
        Regex regex = new Regex(pattern);

        // Match the input string against the regular expression pattern
        MatchCollection matches = regex.Matches(input);

        // Return the count of matched tokens
        return matches.Count;
    }

    static void Main()
    {
        string input = "This is a sample input string.";

        int tokenCount = CountTokens(input);

        Console.WriteLine("Number of tokens in the input string: " + tokenCount);
    }
}
```

