    head: Option<usize>,
    free_list: Option<usize>,
}

impl LinkedList {
    pub fn new() -> Self {
        let mut nodes = [Node { data: 0, next: RefCell::new(None) }; 10];
        let mut free_list = None;

        for i in 0..nodes.len() {
            nodes[i].next.replace(Some(i + 1));
        }

