                        if let Some(file_name) = entry.file_name().to_str() {
                            let parts: Vec<&str> = file_name.split('.').collect();
                            if let Some(program_name) = parts.get(0) {
                                *program_count.entry(*program_name).or_insert(0) += 1;
                            }
                        }
                    }
                }
            }
        }
    }

    let mut top_programs: Vec<(&String, &i32)> = program_count.iter().collect();
    top_programs.sort_by(|a, b| b.1.cmp(a.1));

