import React, { useState, useEffect } from 'react';
import { HyperFormula } from 'hyperformula';
import { BehaviorSubject } from 'rxjs';

const Spreadsheet = () => {
  const [data, setData] = useState({});
  const [formulaSubject] = useState(new BehaviorSubject(''));

  useEffect(() => {
    const hf = HyperFormula.buildEmpty({
      licenseKey: 'YOUR_HYPERFORMULA_LICENSE_KEY'
    });

    const subscription = formulaSubject.subscribe(async (formula) => {
      const result = await hf.evaluateAsync(formula);
      setData(result);
    });

    return () => {
      subscription.unsubscribe();
    };
  }, []);

  const handleFormulaChange = (e) => {
    const formula = e.target.value;
    formulaSubject.next(formula);
  };

  return (
    <div>
      <input type="text" onChange={handleFormulaChange} />
      <div>
        {Object.keys(data).map((key) => (
          <div key={key}>
            {key}: {data[key]}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Spreadsheet;
```

