public class Matchmaking
{
    private List<Player> players;

    public Matchmaking()
    {
        players = new List<Player>();
    }

    public void AddPlayer(Player player)
    {
        players.Add(player);
    }

    public void MatchPlayers(Player player1, Player player2)
    {
        GlickoCalculator calculator = new GlickoCalculator();
        GameInfo gameInfo = new GameInfo();

        Player[] players = { player1, player2 };
        Result[] results = new Result[2];

        results[0] = new Result(players[0].Rating);
        results[1] = new Result(players[1].Rating);

        calculator.UpdateRatings(gameInfo, results);
    }
}
```

