public class FileScanner
{
    public void ScanDirectory(string directoryPath, Action<string> fileAction = null, Action<string> folderAction = null, bool recursive = false)
    {
        try
        {
            // Scan files in the directory
            string[] files = Directory.GetFiles(directoryPath);
            foreach (string file in files)
            {
                fileAction?.Invoke(file);
            }

            // Scan folders in the directory
            string[] folders = Directory.GetDirectories(directoryPath);
            foreach (string folder in folders)
            {
                folderAction?.Invoke(folder);

                if (recursive)
                {
                    ScanDirectory(folder, fileAction, folderAction, recursive);
                }
            }
        }
        catch (Exception e)
        {
            Console.WriteLine($"Error scanning directory: {e.Message}");
        }
    }
}
```

