\begin{algorithm}[H]
 \KwData{Initial state estimate $x_0$ and covariance $P_0$, measurement data $y_1,y_2,...$}
 \KwResult{Updated state estimates $x_k$ and covariances $P_k$}

 \For{$k = 1$ to $N$}{
