import org.bson.Document;

public class SQLtoMongoDBConverter {

    public static Document convertSQLtoMongoDB(String sqlQuery) {
        // Parse the SQL query and convert it to MongoDB query
        Document mongoQuery = new Document();

        // Sample conversion logic
        if (sqlQuery.startsWith("SELECT")) {
            String[] tokens = sqlQuery.split(" ");
            String collectionName = tokens[3];
            String[] fields = tokens[1].split(",");
            Document projection = new Document();
            for (String field : fields) {
                projection.append(field.trim(), 1);
            }
            mongoQuery.append("collection", collectionName)
                      .append("projection", projection);
        } else {
            // Handle other types of SQL queries
        }

        return mongoQuery;
    }

    public static void main(String[] args) {
        String sqlQuery = "SELECT field1, field2 FROM collectionName";
        Document mongoQuery = convertSQLtoMongoDB(sqlQuery);
        System.out.println("MongoDB query: " + mongoQuery.toJson());
    }
}
```

