package main

import (
	"fmt"
	"github.com/go-redis/redis/v8"
	"context"
)

type Object struct {
	Value   string
	Version int
}

func main() {
	ctx := context.Background()

	// Connect to Redis
	rdb := redis.NewClient(&redis.Options{
		Addr: "localhost:6379",
	})

	// Initialize object
	obj := Object{
		Value:   "example value",
		Version: 2,
	}

	// Get current version from Redis
	val, err := rdb.Get(ctx, "object_version").Int()
	if err == nil && obj.Version <= val {
		fmt.Println("Object version is not newer. Aborting.")
		return
	}

	// Store object in Redis
	err = rdb.Set(ctx, "object_value", obj.Value, 0).Err()
	if err != nil {
		fmt.Println(err)
		return
	}

	// Update version in Redis
	err = rdb.Set(ctx, "object_version", obj.Version, 0).Err()
	if err != nil {
		fmt.Println(err)
		return
	}

	fmt.Println("Object stored in Redis.")
}
```

